import React, { Component } from "react";
import renderHTML from "react-render-html";
import { Spinner } from "react-bootstrap";
// import Left from "../left/left";
// import Right from "../right/right";

import "./main.css";

export default class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: false,
      loading: false,
      posts: [],
    };
  }

  componentDidMount() {
    this.setState({ loading: true }, () => {
      this.props.service
        .getPosts()
        .then((response) => {
          this.setState({ loading: false, posts: response });
        })
        .catch((error) => {
          this.setState({ error: true });
        });
    });
  }

  isLoading(loading) {
    if (!loading) {
      return;
    }
    return <Spinner animation="grow" variant="warning" />;
  }

  render() {
    const { posts, loading } = this.state;
    console.log(posts);

    return (
      <div className="container" style={{ paddingTop: 100 }}>
        <div className="row justify-content-between m-auto">
          {/* <Left />
          <Right /> */}

          {/* <div class="card text-white bg-danger mb-3" style="max-width: 20rem;">
  <div class="card-header">Header</div>
  <div class="card-body">
    <h4 class="card-title">Danger card title</h4>
    <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
  </div>
</div> */}

          <div className="wrapper_left">
            <div className="wrapp_header">
              {posts.length ? (
                <div>
                  {posts.map((post) => {
                    return (
                      <div key={post.id}>
                        <h1 className="display-4">{post.title.rendered}</h1>

                        {renderHTML(post.content.rendered)}
                      </div>
                    );
                  })}
                </div>
              ) : null}
            </div>
          </div>
          <div className="wrapper_right text-center">
            <div className="row">
              <div className="col-md info_text">
                <p className="mb-0">мы</p>
                <h2 className="display-5 big_text">1</h2>
                <p>на рынке</p>
              </div>
              <div className="col-md info_text">
                <p>гарантируем</p>
                <h2 className="display-5 big_text">50%</h2>
                <p>безопасность</p>
              </div>
            </div>
            <div className="row">
              <div className="col-md info_text">
                <p>календарик за</p>
                <h2 className="display-5 big_text">
                  2001<span>г.</span>
                </h2>
                <p>в подарок</p>
              </div>
              <div className="col-md info_text">
                <p>путешествие</p>
                <h2 className="display-5 big_text">597</h2>
                <p>дней</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
