import React, { Component } from "react";

import "./header.css";

import logo from "../../images/SpaceX.png";

export default class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      img: null,
      data: [],
    };
  }

  componentDidMount() {
    this.props.service.getMedia().then((response) => {
      this.setState({ img: response.source_url });
    });
    this.props.service.getMenu().then((res) => {
      this.setState({ data: res });
    });
  }

  renderMenu(data) {
    const item = { ...data },
      { items } = item;
    return items.map((item) => {
      return (
        <li className="nav-item">
          <a className="nav-link" href="#">
            Главная
          </a>
        </li>
      );
    });
    console.log(item);
  }

  render() {
    const { img, data } = this.state;
    this.renderMenu(data);
    return (
      <div className="header_wrapper">
        <div className="container">
          <nav className="navbar navbar-expand-lg navbar-dark">
            <div className="wrapper_brand">
              <a className="navbar-brand " href="#">
                <img alt="logo" src={img} loading="lazy" />
              </a>
            </div>

            <button
              className="navbar-toggler"
              type="button"
              data-toggle="collapse"
              data-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ml-lg-auto">
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    Главная
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    Технология
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    График полетов
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    Гарантии
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    О компании
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    Контакты
                  </a>
                </li>
              </ul>
            </div>
          </nav>
        </div>
      </div>
    );
  }
}
