import React, { Component } from "react";
// import Left from "../left/left";
// import Right from "../right/right";

import "./main.css";

export default class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: false,
      loading: false,
      posts: [],
    };
  }

  componentDidMount() {
    this.setState({ loading: true }, () => {
      this.props.service
        .getPosts()
        .then((response) => {
          this.setState({ loading: false, posts: response });
        })
        .catch((error) => {
          this.setState({ error: true });
        });
    });
  }

  render() {
    const { posts } = this.state;
    console.log(posts.length);
    return (
      <div className="container" style={{ paddingTop: 100 }}>
        <div className="row justify-content-between m-auto">
          {/* <Left />
          <Right /> */}
          <div className="wrapper_left">
            <div className="wrapp_header">
              {posts.length ? (
                <div>
                  {posts.map((post) => {
                    return (
                      <div key={post.id}>
                        <h1> {post.title.render}</h1>
                        <p>{}</p>
                      </div>
                    );
                  })}
                </div>
              ) : null}
              <h1 className="display-4">Путешествие</h1>
              на красную планету
            </div>
            <div className="wrapp_btn">
              <button className="btn"> Начать путешествие</button>
            </div>
          </div>
          <div className="wrapper_right text-center">
            <div className="row">
              <div className="col-md info_text">
                <p className="mb-0">мы</p>
                <h2 className="display-5 big_text">1</h2>
                <p>на рынке</p>
              </div>
              <div className="col-md info_text">
                <p>гарантируем</p>
                <h2 className="display-5 big_text">50%</h2>
                <p>безопасность</p>
              </div>
            </div>
            <div className="row">
              <div className="col-md info_text">
                <p>календарик за</p>
                <h2 className="display-5 big_text">
                  2001<span>г.</span>
                </h2>
                <p>в подарок</p>
              </div>
              <div className="col-md info_text">
                <p>путешествие</p>
                <h2 className="display-5 big_text">597</h2>
                <p>дней</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
