import React, { Component } from "react";
import Main from "../pages/main";
import Header from "../header/header";

import "./App.css";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  async componentDidMount() {
    return await fetch(
      "http://localhost:8888/textTasks/wordpress-savigor/wp-json/wp/v2/media"
    )
      .then((res) => res.json())
      .then((response) => {
        this.setState({ data: response });
        console.log(this.state.data);
      });
  }

  render() {
    return (
      <div className="App">
        {/* {this.state.data.map((item, i) => {
          return <img key={i} src={item.source_url} alt="dsf" />;
        })} */}
        <img src={this.state.data[0].source_url} alt="dsf" />
        <Header />
        <Main />
      </div>
    );
  }
}
