import React, { Component } from "react";

import "./header.css";
export default class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      img: null,
      data: [],
    };
  }

  componentDidMount() {
    this.props.service.getMedia().then((response) => {
      this.setState({ img: response.source_url });
      // console.log(this.state);
    });
    this.props.service.getPages("pages").then((res) => {
      this.setState({ data: res });
      // console.log(this.state);
    });
  }

  render() {
    const { img, data } = this.state;
    return (
      <div className="header_wrapper">
        <div className="container">
          <nav className="navbar navbar-expand-lg navbar-dark">
            <div className="wrapper_brand">
              <a className="navbar-brand " href="#">
                <img alt="logo" src={img} loading="lazy" />
              </a>
            </div>
            <button
              className="navbar-toggler"
              type="button"
              data-toggle="collapse"
              data-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ml-lg-auto">
                {data.map((item) => {
                  return (
                    <li key={item.id} className="nav-item">
                      <a className="nav-link" href={item.link}>
                        {item.title.rendered}
                      </a>
                    </li>
                  );
                })}
              </ul>
            </div>
          </nav>
        </div>
      </div>
    );
  }
}
