import React, { Component } from "react";

import "./header.css";

export default class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      img: null,
      data: [],
    };
  }

  componentDidMount() {
    this.setState(() => {
      this.props.service.getMedia().then((response) => {
        return this.setState({ img: response.source_url });
      });
      this.props.service.getMenu().then((res) => {
        return this.setState({ data: res });
      });
    });
  }

  renderMenu(data) {
    const { items } = data;
    console.log(items);
    // let menu;
    // if (data && data.items) {
    //   menu = items.map((item) => {
    //     return (
    //       <li className="nav-item" key={item.id}>
    //         <a className="nav-link" href={item.url}>
    //           {item.title}
    //         </a>
    //       </li>
    //     );
    //   });
    // }
    // return menu;
    const a = items.map((item) => {
      return (
        <a className="nav-link" href={item.url}>
          {item.title}
        </a>
      );
    });
    return <li className="nav-item"> {a}</li>;
  }

  render() {
    const { img, data } = this.state;
    const menu = this.renderMenu(data);
    return (
      <div className="header_wrapper">
        <div className="container">
          <nav className="navbar navbar-expand-lg navbar-dark">
            <div className="wrapper_brand">
              <a className="navbar-brand " href="#">
                <img alt="logo" src={img} loading="lazy" />
              </a>
            </div>

            <button
              className="navbar-toggler"
              type="button"
              data-toggle="collapse"
              data-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ml-lg-auto">
                {menu}
                {/* <li className="nav-item">
                  <a className="nav-link" href="#">
                    Главная
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    Технология
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    График полетов
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    Гарантии
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    О компании
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    Контакты
                  </a>
                </li> */}
              </ul>
            </div>
          </nav>
        </div>
      </div>
    );
  }
}
