import React, { Component } from "react";
import renderHTML from "react-render-html";
import { Spinner } from "react-bootstrap";
// import Left from "../left/left";
// import Right from "../right/right";

import "./main.css";

export default class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: false,
      loading: false,
      posts: [],
    };
  }

  componentDidMount() {
    this.setState({ loading: true }, () => {
      this.props.service
        .getPosts()
        .then((response) => {
          this.setState({ loading: false, posts: response });
        })
        .catch((error) => {
          this.setState({ error: true });
        });
    });
  }

  isLoading(loading) {
    if (!loading) {
      return;
    }
    return <Spinner animation="grow" variant="warning" />;
  }
  isError(error) {
    if (!error) {
      return;
    }
    return (
      <div className="card text-white bg-danger mb-3" style="max-width: 20rem;">
        <div className="card-header">ERROR</div>
        <div className="card-body">
          <h4 className="card-title">Произошла ошибка</h4>
          <p className="card-text">
            Произошла ошибка в загрузке файлов. Пожалуйста, повторите позднее
          </p>
        </div>
      </div>
    );
  }

  render() {
    const { posts, loading, error } = this.state;
    const spinner = this.isLoading(loading),
      isErr = this.isError(error);
    console.log(this.state);
    return (
      <div className="container" style={{ paddingTop: 100 }}>
        {spinner}
        {isErr}
        <div className="row justify-content-between m-auto">
          <div className="wrapper_left">
            <div className="wrapp_header">
              {posts.length ? (
                <div>
                  {posts.map((post) => {
                    return (
                      <div key={post.id}>
                        <h1 className="display-4">{post.title.rendered}</h1>

                        {renderHTML(post.content.rendered)}
                      </div>
                    );
                  })}
                </div>
              ) : null}
            </div>
          </div>
          <div className="wrapper_right text-center">
            <div className="row">
              <div className="col-md info_text">
                <p className="mb-0">мы</p>
                <h2 className="display-5 big_text">1</h2>
                <p>на рынке</p>
              </div>
              <div className="col-md info_text">
                <p>гарантируем</p>
                <h2 className="display-5 big_text">50%</h2>
                <p>безопасность</p>
              </div>
            </div>
            <div className="row">
              <div className="col-md info_text">
                <p>календарик за</p>
                <h2 className="display-5 big_text">
                  2001<span>г.</span>
                </h2>
                <p>в подарок</p>
              </div>
              <div className="col-md info_text">
                <p>путешествие</p>
                <h2 className="display-5 big_text">597</h2>
                <p>дней</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
