export default class TemplateDb {
  constructor() {
    this._apiBase =
      "http://localhost:8888/textTasks/wordpress-savigor/wp-json/wp/v2/";
  }
  getRes = async (url) => {
    const res = await fetch(`${this._apiBase}${url}`);
    if (!res.ok) {
      throw new Error(`Could not fetch ${url}, status : ${res.status}`);
    }
    return res.json();
  };

  getMedia = async () => {
    const res = await this.getRes("media/16");
    return res;
  };
  getPosts = async () => {
    const res = await this.getRes("posts");
    return res;
  };
}
